$input-size: 1.5rem;

// * {
//   border: 0; margin: 0; padding: 0;
// }

/* Chrome, Safari, Edge, Opera */
input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

/* Firefox */
input[type=number] {
  -moz-appearance: textfield;
}



.input {
  &--decimal {
    display: flex;
    flex-direction: column;
  }

  &--fractional {
    display: grid;
    justify-items: center;
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-areas: 
      "whl numl denl"
      "whi numi deni";

    // @media screen and (max-width: 768px) {
    //   grid-template-columns: 1fr 1fr;
    //   grid-template-areas: 
    //     "whl whl"
    //     "whi whi"
    //     "numl denl"
    //     "numi deni";
    // }
  }

  &__resolution {
    align-self: center;
    display: flex;
    flex-direction: column;
  }

  &__whole_label {
    grid-area: whl;
  }

  &__numerator_label {
    grid-area: numl;
  }

  &__denominator_label {
    grid-area: denl;
  }

  &__whole_input {
    grid-area: whi;
    font-size: $input-size;
    display: flex;
    flex-direction: column;
    align-items: center;

    @media screen and (max-width: 768px) {
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: center;
    }
  }

  &__numerator_input {
    grid-area: numi;
    font-size: $input-size;
    display: flex;
    flex-direction: column;
  }

  &__denominator_input {
    grid-area: deni;
    font-size: $input-size;
    display: flex;
    flex-direction: column;
  }
}

.output {
  font-size: $input-size;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;

  &--alert {
    background-color: black;
    color: yellow;
    font-size: $input-size * 1.5;
    text-align: center;
  }
}

.words {
  font-size: 1.5rem;
  font-family: sans-serif;
  justify-content: center;
  text-align: center;
}

.button {
  display: block;
  font-size: $input-size;
  margin: 0.5rem;
  padding: 0.8rem;
  min-width: 10rem;
  border-radius: 1rem;

  &--inc {
    background-color: palegreen;
  }

  &--dec {
    background-color: lightblue;
  }
}
.input_box {
  font-size: 2.5rem;
  height: 3rem;
  width: 8rem;
  text-align: center;
  flex-basis: 65%;
  border-radius: 1rem;
}

.slider {
  font-size: 3rem;
  height: 3rem;
}

.title {
  font-size: 1.5rem;
  background-color: black;
  border-radius: 1rem;
  color: lightblue;
  padding: 0.5rem;
  text-justify: center;
}

.links {
  margin-top: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;

  &__link {
    text-decoration: none;
  }
}
